
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		int b = 5;
		int c = 3;

		a = b + c;
		System.out.printf("%d + %d = %d0\n", b, c, a );
	
		a= b - c;
		System.out.printf("%d - %d = %d\n", b, c, a );
		
		a= b * c;
		System.out.printf("%d * %d = %d\n", b, c, a );
		
		a= b / c;
		System.out.printf("%d / %d = %d\n", b, c, a );
		
		a= b % c;
		System.out.printf("%d % %d = %d\n", b, c, a );
	}

}
